import React from "react";
import "../stylesheets/animation.css";

const animation = (props) => {
  let animationPaths = ["home/index.html", "Fighter/index.html"];

  return (
    <div className="ANOuterDiv">
      {animationPaths.map((animationPath) => {
        let index = animationPaths.indexOf(animationPath) + 1;
        return (
          <iframe
            className={
              props.popOut === index
                ? "Animation popOutRight"
                : props.popOut + 1 !== index
                ? "Animation DisplayNone"
                : "Animation"
            }
            id={"HomeAnimation" + index}
            src={animationPath}
            height="650px"
            width="800px"
            frameBorder="0"
          ></iframe>
        );
      })}
    </div>
  );
};

export default animation;
