import React, { useState } from "react";
const QuestionBox = () => {
  let [questions, setQuestions] = useState([
    "Father Elephant is aged three times more than his son. After 8 years, he would be two and a half times of his son’s age. After further 8 years, how many times would he be of his son’s age?",
  ]);

  return (
    <div className="QBMainDiv">
      <div className="QBSituationDiv">
        <span>
          Hey Nagveer, meet John. John likes to explore undiscovered places ,so
          he decided to visit a hotel today. Unfortunately he couldn’t remember
          the name of the hotel. Help John in remembering the name of the hotel
          by answering the question mentioned below.
        </span>
      </div>
      <div className="QBQuestionDiv">
        <div className="QBQuestion">{"Q. " + questions[0]}</div>
        <div className="QBOptions"></div>
      </div>
      <div className="QBButtonDiv"></div>
    </div>
  );
};

export default QuestionBox;
